import React, { useEffect, useState } from 'react';
import { sendWebSocketMessage } from '../websocket';

const ToDoList = ({ tasks, onEditTask, onDeleteTask, isDeleteMode, userId }) => {
  const [localTasks, setLocalTasks] = useState(tasks);

  useEffect(() => {
    setLocalTasks(tasks);
  }, [tasks]);

  const handleCheckboxChange = (task) => {
    const updatedTask = {
      ...task,
      completed: !task.completed,
    };
    setLocalTasks((prevTasks) =>
      prevTasks.map((t) => (t.taskId === updatedTask.taskId ? updatedTask : t))
    );
    sendWebSocketMessage('/app/updateTask', {
      type: 'TASK_UPDATE',
      task: updatedTask,
    });
  };

  return (
    <div>
      <ul>
        {localTasks.map((task) => (
          <li key={task.taskId}>
            <input
              type="checkbox"
              checked={task.completed}
              onChange={() => handleCheckboxChange(task)}
            />
            {task.text}
            <button onClick={() => onEditTask(task)}>Edit</button>
            {isDeleteMode && <button onClick={() => onDeleteTask(task.taskId)}>Delete</button>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ToDoList;
