import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import { fetchTasks, addTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories, deleteCategory } from '../api';
import { connectWebSocket, disconnectWebSocket, sendUpdateTaskMessage, sendUpdateCategoryMessage } from '../websocket';
import { iconMap, customCategoryIcon } from '../icons';

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  const initializeDashboard = useCallback(async () => {
    try {
      const email = await fetchEmail();
      const fetchedUserId = await fetchUserId(email);

      if (fetchedUserId) {
        setUserId(fetchedUserId);

        const fetchedTasks = await fetchTasks();
        setTasks(fetchedTasks);

        const fetchedCategories = await fetchCategories(fetchedUserId);
        if (Array.isArray(fetchedCategories)) {
          const uniqueCategories = fetchedCategories.reduce((acc, category) => {
            if (!acc.some(cat => cat.categoryId === category.categoryId)) {
              acc.push({
                ...category,
                categoryId: category.categoryId,
                icon: iconMap[category.name] || customCategoryIcon,
              });
            }
            return acc;
          }, []);
          setCategories(uniqueCategories);

          if (uniqueCategories.length > 0) {
            setSelectedCategory(uniqueCategories[0]);
          } else {
            setSelectedCategory(null); // Set to null if no categories exist
          }
        } else {
          console.error('Fetched categories is not an array:', fetchedCategories);
          setCategories([]);
          setSelectedCategory(null);
        }
      } else {
        navigate('/login');
      }
    } catch (error) {
      console.error('Error initializing dashboard:', error);
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    initializeDashboard();
  }, [initializeDashboard]);

  useEffect(() => {
    const handleWebSocketMessage = (message) => {
      console.log('Received WebSocket message:', message);

      const { type, taskId, categoryId, name, sharedUserIds, ...updatedData } = message;

      setTasks(prevTasks => {
        if (type === 'TASK_UPDATE') {
          const taskIndex = prevTasks.findIndex(task => task.taskId === taskId);
          const taskExists = taskIndex !== -1;
          const isTaskSharedWithUser = sharedUserIds?.includes(userId);

          if (taskExists || isTaskSharedWithUser) {
            const updatedTasks = [...prevTasks];
            if (taskExists) {
              updatedTasks[taskIndex] = { ...prevTasks[taskIndex], ...updatedData };
            } else {
              updatedTasks.push({ taskId, ...updatedData });
            }
            return updatedTasks;
          }
          return prevTasks;
        }
        return prevTasks;
      });

      setCategories(prevCategories => {
        if (type === 'CATEGORY_UPDATE') {
          const categoryIndex = prevCategories.findIndex(category => category.categoryId === categoryId);
          if (categoryIndex !== -1) {
            prevCategories[categoryIndex] = { ...prevCategories[categoryIndex], ...updatedData, name };
          } else {
            prevCategories.push({ categoryId, ...updatedData, name });
          }
          return [...prevCategories];
        }
        return prevCategories;
      });
    };

    if (userId) {
      console.log('Connecting WebSocket for user ID:', userId);

      const fetchIds = async () => {
        try {
          const fetchedTasks = await fetchTasks();
          const fetchedCategories = await fetchCategories(userId);
          const taskIds = fetchedTasks.map(task => task.taskId);
          const categoryIds = Array.isArray(fetchedCategories) ? fetchedCategories.map(category => category.categoryId) : [];
          connectWebSocket(userId, taskIds, categoryIds, handleWebSocketMessage);
        } catch (error) {
          console.error('Error fetching IDs for WebSocket:', error);
        }
      };

      fetchIds();

      return () => {
        console.log('Disconnecting WebSocket for user ID:', userId);
        disconnectWebSocket();
      };
    }
  }, [userId]);

  const handleAddNewTask = async (newTask) => {
    if (!selectedCategory) return;

    try {
      const formattedTask = {
        ...newTask,
        categoryId: selectedCategory.categoryId,
        userId: userId
      };
      console.log("Formatted Task before sending to backend:", formattedTask);

      const addedTask = await addTask(formattedTask, userId);
      setShowAddTaskPopUp(false);

      setTasks(prevTasks => {
        const uniqueTasks = [...prevTasks, addedTask].filter((task, index, self) =>
          index === self.findIndex((t) => t.taskId === task.taskId)
        );
        return uniqueTasks;
      });
      sendUpdateTaskMessage(addedTask);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    if (!selectedCategory) return;

    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
        categoryId: selectedCategory?.categoryId || currentTask.categoryId,
      };
      console.log("Task to Update before sending to backend:", taskToUpdate);
      setShowEditTaskPopUp(false);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.map(task => (task.taskId === taskToUpdate.taskId ? taskToUpdate : task));
        return updatedTasks.filter((task, index, self) =>
          index === self.findIndex((t) => t.taskId === task.taskId)
        );
      });
      sendUpdateTaskMessage(taskToUpdate);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddCategory = async (newCategory) => {
    try {
      const categoryExists = categories.some(cat => cat.name === newCategory.name);
      if (categoryExists) {
        console.warn('Category already exists:', newCategory);
        return;
      }
      const data = await addCategory(newCategory);
      const newCategoryObject = {
        categoryId: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
      };
      sendUpdateCategoryMessage(newCategoryObject);
      setCategories(prevCategories => [...prevCategories, newCategoryObject]);
      setSelectedCategory(newCategoryObject);
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const handleDeleteCategory = async (id, name) => {
    try {
      await deleteCategory(id);
      setCategories(prevCategories => prevCategories.filter(category => category.categoryId !== id));
      sendUpdateCategoryMessage({ categoryId: id, name: name, message: "Category deleted successfully" });
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const categorizedTasks = tasks.reduce((acc, task) => {
    const categoryId = task.categoryId;
    if (!acc[categoryId]) acc[categoryId] = [];
    acc[categoryId].push(task);
    return acc;
  }, {});

  const filteredTasks = categorizedTasks[selectedCategory?.categoryId] || [];

  useEffect(() => {
    console.log('Selected Category:', selectedCategory);
  }, [selectedCategory]);

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between mb-4">
          <Navbar />
          <ToDoActions
            toggleDeleteMode={toggleDeleteMode}
            isDeleteMode={isDeleteMode}
            setShowAddTaskPopUp={setShowAddTaskPopUp}
            setShowAddCategoryPopUp={setShowAddCategoryPopUp}
          />
        </div>
        <div className="flex gap-4">
          <Categories
            categories={categories}
            selectedCategory={selectedCategory}
            setSelectedCategory={setSelectedCategory}
            handleAddCategory={handleAddCategory}
            handleDeleteCategory={handleDeleteCategory}
          />
          <ToDoList
            tasks={filteredTasks}
            isDeleteMode={isDeleteMode}
            handleEditTask={handleEditTask}
            handleDeleteTask={handleDeleteTask}
          />
        </div>
        {showAddTaskPopUp && (
          <AddTaskPopUp
            onClose={() => setShowAddTaskPopUp(false)}
            onAddTask={handleAddNewTask}
          />
        )}
        {showEditTaskPopUp && (
          <EditTaskPopUp
            task={currentTask}
            onClose={() => setShowEditTaskPopUp(false)}
            onSaveTask={handleSaveTask}
          />
        )}
        {showAddCategoryPopUp && (
          <AddCategoryPopUp
            onClose={() => setShowAddCategoryPopUp(false)}
            onAddCategory={handleAddCategory}
          />
        )}
      </div>
    </div>
  );
}

export default Dashboard;
