import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import { fetchTasks, addTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories } from '../api';
import { connectWebSocket, disconnectWebSocket, sendUpdateTaskMessage, sendWebSocketMessage } from '../websocket';
import { categorizeTasks, STANDARD_CATEGORIES } from '../utils/categorizeTasks';
import { iconMap, customCategoryIcon } from '../icons'; // Make sure to import here

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(STANDARD_CATEGORIES);
  const [selectedCategory, setSelectedCategory] = useState(STANDARD_CATEGORIES[0]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  const initializeDashboard = useCallback(async () => {
    try {
      const email = await fetchEmail();
      console.log('Fetched Email:', email); // Debugging

      const fetchedUserId = await fetchUserId(email);
      console.log('Fetched UserId:', fetchedUserId); // Debugging

      if (fetchedUserId) {
        setUserId(fetchedUserId);

        const fetchedTasks = await fetchTasks();
        console.log('Fetched Tasks:', fetchedTasks); // Debugging
        setTasks(fetchedTasks);

        const fetchedCategories = await fetchCategories(fetchedUserId);
        console.log('Fetched Categories:', fetchedCategories); // Debugging
        if (Array.isArray(fetchedCategories)) {
          const customCategories = fetchedCategories.map(category => ({
            ...category,
            id: category.categoryId,
            icon: iconMap[category.name] || customCategoryIcon,
          }));
          setCategories([...STANDARD_CATEGORIES, ...customCategories]);

          if (customCategories.length > 0) {
            setSelectedCategory(customCategories[0]);
          }
        } else {
          console.error('Fetched categories is not an array:', fetchedCategories);
          setCategories(STANDARD_CATEGORIES); // Fallback to standard categories
        }
      } else {
        navigate('/login');
      }
    } catch (error) {
      console.error('Error initializing dashboard:', error);
      navigate('/login');
    }
  }, [navigate]);


  useEffect(() => {
    initializeDashboard();
  }, [initializeDashboard]);

  useEffect(() => {
    const handleWebSocketMessage = (message) => {
      console.log('Received WebSocket message:', message);

      const { type, taskId, categoryId, sharedUserIds, ...updatedTaskData } = message;

      if (type === 'TASK_UPDATE') {
        console.log('Handling TASK_UPDATE:', { taskId, categoryId, updatedTaskData });

        setTasks(prevTasks => {
          const taskIndex = prevTasks.findIndex(task => task.taskId === taskId);
          const taskExists = taskIndex !== -1;
          const isTaskSharedWithUser = sharedUserIds?.includes(userId);

          if (taskExists || isTaskSharedWithUser) {
            const updatedTasks = [...prevTasks];
            if (taskExists) {
              updatedTasks[taskIndex] = { ...prevTasks[taskIndex], ...updatedTaskData };
            } else {
              updatedTasks.push({ taskId, ...updatedTaskData });
            }
            return updatedTasks;
          } else {
            return prevTasks;
          }
        });
      } else if (type === 'CATEGORY_UPDATE' || type === 'CATEGORY_SHARE') {
        console.log('Handling CATEGORY_UPDATE or CATEGORY_SHARE:', { categoryId, updatedTaskData });

        setCategories(prevCategories => {
          const categoryIndex = prevCategories.findIndex(category => category.categoryId === categoryId);
          const updatedCategories = [...prevCategories];

          if (categoryIndex !== -1) {
            updatedCategories[categoryIndex] = { ...prevCategories[categoryIndex], ...updatedTaskData };
          } else {
            updatedCategories.push({ categoryId, ...updatedTaskData });
          }

          return updatedCategories;
        });
      }
    };


    if (userId) {
      console.log('Connecting WebSocket for user ID:', userId);

      // Fetch all task and category IDs to subscribe to specific topics
      const fetchIds = async () => {
        const fetchedTasks = await fetchTasks();
        const fetchedCategories = await fetchCategories(userId);
        const taskIds = fetchedTasks.map(task => task.taskId);
        const categoryIds = Array.isArray(fetchedCategories) ? fetchedCategories.map(category => category.categoryId) : [];
        console.log('Fetched Task IDs:', taskIds); // Debugging
        console.log('Fetched Category IDs:', categoryIds); // Debugging
        connectWebSocket(userId, taskIds, categoryIds, handleWebSocketMessage);
      };

      fetchIds();

      return () => {
        console.log('Disconnecting WebSocket for user ID:', userId);
        disconnectWebSocket();
      };
    }
  }, [userId]);


  const handleAddNewTask = async (newTask) => {
    if (!selectedCategory) return;

    try {
      console.log("Task data before sending:", newTask);
      console.log("Selected Category ID:", selectedCategory.id);

      const formattedTask = {
        ...newTask,
        categoryId: selectedCategory.id,
        userId: userId
      };
      console.log("Formatted Task:", formattedTask);

      const addedTask = await addTask(formattedTask, userId);
      console.log("Added Task from backend:", addedTask);

      if (addedTask.categoryId !== selectedCategory.id) {
        console.error(`Mismatch: Task Category ID ${addedTask.categoryId} does not match Selected Category ID ${selectedCategory.id}`);
      }

      setShowAddTaskPopUp(false);

      setTasks(prevTasks => {
        const uniqueTasks = [...prevTasks, addedTask].filter((task, index, self) =>
          index === self.findIndex((t) => t.taskId === task.taskId)
        );
        return uniqueTasks;
      });
      sendUpdateTaskMessage(addedTask);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };




  const handleSaveTask = async (updatedTask) => {
    if (!selectedCategory) return;

    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
        categoryId: selectedCategory?.id || currentTask.categoryId,
      };
      console.log("Task to Update before sending to backend:", taskToUpdate);
      setShowEditTaskPopUp(false);
      setTasks(prevTasks => {
        const updatedTasks = prevTasks.map(task => (task.taskId === taskToUpdate.taskId ? taskToUpdate : task));
        return updatedTasks.filter((task, index, self) =>
          index === self.findIndex((t) => t.taskId === task.taskId)
        );
      });
      sendUpdateTaskMessage(taskToUpdate);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const handleAddCategory = async (newCategory) => {
    try {
      const categoryExists = categories.some(cat => cat.name === newCategory.name);
      if (categoryExists) {
        console.warn('Category already exists:', newCategory);
        return;
      }
      const data = await addCategory(newCategory);
      const newCategoryObject = {
        id: data.categoryId,
        name: data.name,
        icon: customCategoryIcon,
      };
      sendWebSocketMessage(`/app/updateCategory/${newCategoryObject.id}`, { type: 'CATEGORY_UPDATE', category: newCategoryObject });
      setCategories(prevCategories => [...prevCategories, newCategoryObject]);
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const categorizedTasks = categorizeTasks(tasks, categories);
  const filteredTasks = categorizedTasks[selectedCategory?.id] || [];

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories onSelectCategory={setSelectedCategory} userId={userId} categoriesFromBackend={categories} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory && selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />}
              <span className="font-bold text-grayCustom-dark">{selectedCategory?.name}</span>
            </div>
            <ToDoList
              todos={filteredTasks}
              setTasks={setTasks}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask}
              selectedCategory={selectedCategory}
              handleDeleteTask={handleDeleteTask}
              userId={userId}
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              selectedCategoryId={selectedCategory?.id}
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)}
              userId={userId}
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          categories={categories}
          onClose={() => setShowAddTaskPopUp(false)}
        />
      )}
      {showEditTaskPopUp && (
        <EditTaskPopUp
          task={currentTask}
          onSaveTask={handleSaveTask}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
        />
      )}
    </div>
  );
}

export default Dashboard;
