import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:8080', // Deine Basis-URL
  headers: {
    'Content-Type': 'application/json',
  },
});

api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('jwtToken'); // Holen Sie den Token aus dem Local Storage
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export const registerUser = async (user) => {
  try {
    const response = await api.post('/auth/register', user);
    return response.data;
  } catch (error) {
    console.error('Error registering user:', error);
    throw error;
  }
};

export const loginUser = async (credentials) => {
  try {
    const response = await api.post('/auth/login', credentials);
    const accessToken = response.data;
    if (accessToken) {
      localStorage.setItem('jwtToken', accessToken);
    }
    return { accessToken };
  } catch (error) {
    console.error('Error logging in:', error);
    throw error;
  }
};

export const fetchEmail = async () => {
  try {
    const response = await api.get('/auth/email');
    return response.data;
  } catch (error) {
    console.error('Error fetching email:', error);
    throw new Error('Fetching email failed');
  }
};

export const fetchUserId = async (email) => {
  try {
    const response = await api.get(`/api/v1/user/useridByEmail?email=${email}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user ID:', error);
    throw new Error('Fetching user ID failed');
  }
};

export const fetchTasks = async () => {
  try {
    console.log('Fetching tasks'); // Debugging
    const response = await api.get('/api/v1/task/getTasks');
    console.log('Fetched tasks:', response.data); // Debugging
    return response.data;
  } catch (error) {
    console.error('Error fetching tasks:', error);
    throw error;
  }
};

export const addTask = async (task, userId) => {
  try {
    // Stelle sicher, dass die userId im task-Objekt gesetzt ist
    const response = await api.post('/api/v1/task/save', { ...task, userId });
    return response.data;
  } catch (error) {
    console.error('Error adding task:', error);
    throw error;
  }
};

export const updateTask = async (task, userId) => {
  try {
    // Stelle sicher, dass die userId im task-Objekt gesetzt ist
    const response = await api.put('/api/v1/task/update', { ...task, userId });
    return response.data;
  } catch (error) {
    console.error('Error updating task:', error);
    throw error;
  }
};

export const deleteTask = async (id) => {
  try {
    const response = await api.delete(`/api/v1/task/delete/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting task:', error);
    throw error;
  }
};

export const fetchUser = async (userId) => {
  try {
    const response = await api.get(`/api/v1/user/${userId}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching user:', error);
    throw error;
  }
};

export const updateUser = async (user) => {
  try {
    const response = await api.put('/api/v1/user/update', user);
    return response.data;
  } catch (error) {
    console.error('Error updating user:', error);
    throw error;
  }
};

export const deleteUser = async (id) => {
  try {
    const response = await api.delete(`/api/v1/user/deleteuser/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting user:', error);
    throw error;
  }
};

export const addCategory = async (category) => {
  try {
    console.log('Adding category:', category);
    const response = await api.post(`/api/v1/category/save`, category);
    console.log('Category added:', response.data);
    return response.data;
  } catch (error) {
    console.error('Error adding category:', error);
    console.error('Error details:', error.response?.data);
    throw error;
  }
};

export const deleteCategory = async (categoryId) => {
  try {
    const response = await api.delete(`/api/v1/category/delete/${categoryId}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting category:', error);
    throw error;
  }
};

export const generateTokenForCategory = async (categoryId, userId) => {
  try {
    const response = await api.post('/api/v1/token/generateForCategory', { categoryId, userId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for category:', error);
    throw error;
  }
};

export const generateTokenForTask = async (taskId, userId) => {
  try {
    const response = await api.post('/api/v1/token/generateForTask', { taskId, userId });
    return response.data;
  } catch (error) {
    console.error('Error generating token for task:', error);
    throw error;
  }
};

export const logoutUser = async () => {
  try {
    // Entferne Tokens oder andere Authentifizierungsdaten aus dem lokalen Speicher
    localStorage.removeItem('jwtToken');
    sessionStorage.removeItem('jwtToken');
  } catch (error) {
    console.error('Error logging out:', error);
    throw error;
  }
};
