import { Stomp } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
    constructor() {
        this.stompClient = null;
    }

    connect(onMessageReceived) {
        const socket = new SockJS(SOCKET_URL);
        this.stompClient = Stomp.over(socket);

        const token = localStorage.getItem('jwtToken'); // JWT-Token aus Local Storage holen

        this.stompClient.connect(
            { Authorization: `Bearer ${token}` },
            () => {
                console.log('Connected to WebSocket');

                // Abonniere Kategorie-Updates
                this.stompClient.subscribe('/topic/category/1', (message) => { // Ersetze 1 mit der tats채chlichen Kategorie-ID
                    if (message.body) {
                        try {
                            const parsedMessage = JSON.parse(message.body);
                            onMessageReceived('category', parsedMessage);
                        } catch (error) {
                            console.error('Failed to parse category message as JSON:', error);
                        }
                    }
                });

                // Abonniere Aufgaben-Updates
                this.stompClient.subscribe('/topic/task/1', (message) => { // Ersetze 1 mit der tats채chlichen Aufgaben-ID
                    if (message.body) {
                        try {
                            const parsedMessage = JSON.parse(message.body);
                            onMessageReceived('task', parsedMessage);
                        } catch (error) {
                            console.error('Failed to parse task message as JSON:', error);
                        }
                    }
                });

                // Abonniere Greetings-Nachrichten
                this.stompClient.subscribe('/topic/greetings', (message) => {
                    if (message.body) {
                        try {
                            const parsedMessage = JSON.parse(message.body);
                            onMessageReceived('greetings', parsedMessage);
                        } catch (error) {
                            console.error('Failed to parse greetings message as JSON:', error);
                        }
                    }
                });
            },
            (error) => {
                console.error('Error connecting to WebSocket:', error);
                setTimeout(() => this.connect(onMessageReceived), 5000); // Reconnect after 5 seconds
            }
        );
    }

    disconnect() {
        if (this.stompClient) {
            this.stompClient.disconnect(() => {
                console.log('Disconnected from WebSocket');
            });
        }
    }

    sendMessage(destination, message) {
        if (this.stompClient && this.stompClient.connected) {
            this.stompClient.send(destination, {}, JSON.stringify(message));
        }
    }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (onMessageReceived) => {
    webSocketService.connect(onMessageReceived);
};

export const disconnectWebSocket = () => {
    webSocketService.disconnect();
};

export const sendWebSocketMessage = (destination, message) => {
    webSocketService.sendMessage(destination, message);
};
