export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },
];

export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Erstellen Sie ein Set von Kategorie-IDs für einen schnellen Zugriff
  const categoryIds = new Set(categories.map(category => category.id));

  // Durchlaufen Sie alle Kategorien
  categories.forEach(category => {
    if (!categoryIds.has(category.id)) {
      // Wenn die Kategorie nicht in den Standardkategorien ist, aber Aufgaben zugewiesen wurden
      categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
    } else {
      // Standardkategorien
      switch (category.id) {
        case 1:
          // All Tasks
          categorizedTasks[category.id] = tasks;
          break;
        case 2:
          // Today
          categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
          break;
        case 3:
          // Planned
          categorizedTasks[category.id] = tasks.filter(task => task.planDate);
          break;
        case 4:
          // Any Time (just a folder, no tasks)
          categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
          break;
        case 5:
          // Work
          categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
          break;
        case 6:
          // Personal
          categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
          break;
        case 7:
          // Logbook
          categorizedTasks[category.id] = tasks.filter(task => task.completed);
          break;
        default:
          categorizedTasks[category.id] = tasks.filter(task => task.categoryId === category.id);
          break;
      }
    }
  });

  return categorizedTasks;
};
