import React, { useState, useEffect } from 'react';
import ToDo from './ToDo';
import { startOfToday, isAfter } from 'date-fns';
import { deleteTask } from '../api'; // REST-API für das Löschen
import { sendWebSocketMessage } from '../websocket'; // WebSocket-Funktion für das Update

export default function ToDoList({ todos = [], setTasks, isDeleteMode, onEditTodo, selectedCategory }) {
  const [selectedTodoId, setSelectedTodoId] = useState(null);
  const [localTodos, setLocalTodos] = useState(todos);

  useEffect(() => {
    setLocalTodos(todos);
    console.log('Todos updated:', todos);
  }, [todos]);

  const handleCheckboxChange = async (id) => {
    console.log('Checkbox changed for ID:', id);
    const updatedTodos = localTodos.map(todo => {
      if (todo.taskId === id) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; // Kategorie-ID auf 7 setzen, wenn die Aufgabe abgeschlossen ist
        }
        return updatedTodo;
      }
      return todo;
    });
    setLocalTodos(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      sendWebSocketMessage('/app/updateTask', updatedTask);
      console.log('WebSocket update task successful');
    } catch (error) {
      console.error('Error updating task via WebSocket:', error);
    }
  };

  const handleDateChange = async (id, date, purpose) => {
    console.log(`Updating ${purpose} date for ID:`, id);
    const updatedTodos = localTodos.map(todo =>
      todo.taskId === id ? { ...todo, [`${purpose}Date`]: date.toISOString() } : todo
    );
    setLocalTodos(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      sendWebSocketMessage('/app/updateTask', updatedTask);
      console.log('WebSocket update task successful');
    } catch (error) {
      console.error('Error updating task via WebSocket:', error);
    }
  };

  const handleDeleteClick = async (id) => {
    console.log('Deleting task with ID:', id);
    try {
      await deleteTask(id);
      console.log('Task deleted successfully');

      // Entferne die Aufgabe aus den lokalen Aufgaben
      const updatedTodos = localTodos.filter(todo => todo.taskId !== id);
      setLocalTodos(updatedTodos);
      setTasks(updatedTodos); // Aktualisiere die Aufgaben im übergeordneten Zustand
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const today = startOfToday();

  const filteredTodos = localTodos.filter(todo => {
    switch (selectedCategory.name) {
      case 'Planned':
        return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
      case 'Today':
      case 'Personal':
      case 'Work':
      case 'Any Time':
        return todo.categoryId === selectedCategory.id;
      case 'All Tasks':
        return !todo.planDate && todo.categoryId !== 7;
      case 'Logbook':
        return todo.categoryId === 7;
      default:
        return todo.categoryId === selectedCategory.id;
    }
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.taskId}
            todo={todo}
            handleCheckboxChange={() => handleCheckboxChange(todo.taskId)}
            handleTodoClick={() => setSelectedTodoId(todo.taskId)}
            handleDelete={() => handleDeleteClick(todo.taskId)}
            handleEditClick={() => onEditTodo(todo)}
            isDeleteMode={isDeleteMode}
            onDateChange={handleDateChange}
            isSelected={todo.taskId === selectedTodoId}
          />
        ))}
      </div>
    </fieldset>
  );
}
