export const STANDARD_CATEGORIES = [
  { id: 1, name: 'All Tasks' },
  { id: 2, name: 'Today' },
  { id: 3, name: 'Planned' },
  { id: 4, name: 'Any Time' },
  { id: 5, name: 'Work' },
  { id: 6, name: 'Personal' },
  { id: 7, name: 'Logbook' },
];

export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  // Initialize categorizedTasks for each category
  categories.forEach(category => {
    categorizedTasks[category.categoryId] = [];
  });

  // Add standard categories
  STANDARD_CATEGORIES.forEach(category => {
    categorizedTasks[category.id] = [];
  });

  // Filter tasks based on category rules
  tasks.forEach(task => {
    const { categoryId, completed, planDate, taskId } = task;

    // Always add task to its specific category
    if (!categorizedTasks[categoryId]) {
      categorizedTasks[categoryId] = [];
    }
    categorizedTasks[categoryId].push({ ...task, key: `${categoryId}-${taskId}` });

    // Add task to standard categories based on rules
    if (categoryId !== 7) {
      categorizedTasks[1].push({ ...task, key: `1-${taskId}` }); // All Tasks (excluding Logbook)
    }

    if (completed) {
      categorizedTasks[7].push({ ...task, key: `7-${taskId}` }); // Logbook
    } else {
      if (categoryId === 2) {
        categorizedTasks[2].push({ ...task, key: `2-${taskId}` }); // Today
      }
      if (planDate) {
        categorizedTasks[3].push({ ...task, key: `3-${taskId}` }); // Planned
      }
      if (categoryId === 4) {
        categorizedTasks[4].push({ ...task, key: `4-${taskId}` }); // Any Time
      }
      if (categoryId === 5) {
        categorizedTasks[5].push({ ...task, key: `5-${taskId}` }); // Work
      }
      if (categoryId === 6) {
        categorizedTasks[6].push({ ...task, key: `6-${taskId}` }); // Personal
      }
    }
  });

  return categorizedTasks;
};
