import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';

const SOCKET_URL = 'http://localhost:8080/ws';

class WebSocketService {
  constructor() {
    this.stompClient = null;
    this.connected = false;
    this.reconnectDelay = 5000;
    this.messageQueue = [];
  }

  connect(userId, onTaskMessageReceived, onCategoryMessageReceived) {
    if (!userId) {
      console.error('Cannot connect, userId is not provided.');
      return;
    }

    if (this.connected) {
      console.log('Already connected to WebSocket');
      return;
    }

    const socket = new SockJS(SOCKET_URL);
    this.stompClient = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: this.reconnectDelay,
      onConnect: () => {
        console.log('WebSocket connected');
        this.connected = true;
        this.stompClient.subscribe(`/user/${userId}/queue/tasks`, (message) => {
          onMessageReceived(JSON.parse(message.body));
        });
        this.stompClient.subscribe(`/user/${userId}/queue/categories`, (message) => {
          onCategoryMessageReceived(JSON.parse(message.body));
        });
        this.processQueue();
      },
      onStompError: (frame) => {
        console.error('Broker error: ', frame.headers['message']);
        console.error('Additional details: ', frame.body);
      },
      onWebSocketError: (event) => {
        console.error('WebSocket error: ', event);
      },
      onDisconnect: () => {
        console.log('WebSocket disconnected');
        this.connected = false;
      }
    });

    this.stompClient.activate();
  }

  disconnect() {
    if (this.stompClient) {
      this.stompClient.deactivate();
      console.log('WebSocket client deactivated');
    }
  }

  sendMessage(destination, message) {
    if (this.connected) {
      this.stompClient.publish({
        destination: destination,
        body: JSON.stringify(message),
      });
    } else {
      this.messageQueue.push({ destination, message });
    }
  }

  processQueue() {
    while (this.messageQueue.length > 0) {
      const { destination, message } = this.messageQueue.shift();
      this.sendMessage(destination, message);
    }
  }
}

const webSocketService = new WebSocketService();

export const connectWebSocket = (userId, onMessageReceived, onCategoryMessageReceived) => {
  webSocketService.connect(userId, onMessageReceived, onCategoryMessageReceived);
};

export const disconnectWebSocket = () => {
  webSocketService.disconnect();
};

export const sendWebSocketMessage = (destination, message) => {
  webSocketService.sendMessage(destination, message);
};
