import React from 'react';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import { useDashboard } from '../hooks/useDashboard';

function Dashboard() {
  const {
    showEditTaskPopUp,
    showAddTaskPopUp,
    showAddCategoryPopUp,
    isDeleteMode,
    currentTask,
    setCurrentTask,
    setShowEditTaskPopUp,
    setShowAddTaskPopUp,
    setShowAddCategoryPopUp,
    setIsDeleteMode,
    handleAddTask,
    handleDeleteTask,
    handleCheckboxChange,
    handleDateChange,
    handleAddCategory,
    tasks,
    categories,
    selectedCategory,
    setSelectedCategory,
  } = useDashboard();

  return (
    <div className="dashboard">
      <Navbar />
      <div className="dashboard-content">
        <Categories
          categories={categories}
          selectedCategory={selectedCategory}
          onSelectCategory={setSelectedCategory}
          onAddCategory={handleAddCategory}
        />
        <div className="todo-section">
          <ToDoActions
            toggleDeleteMode={() => setIsDeleteMode(!isDeleteMode)}
            showAddTaskPopUp={() => setShowAddTaskPopUp(true)}
            showAddCategoryPopUp={() => setShowAddCategoryPopUp(true)}
            selectedCategory={selectedCategory}
            handleAddCategory={handleAddCategory}
          />
          <ToDoList
            todos={tasks}
            isDeleteMode={isDeleteMode}
            onEditTodo={(task) => {
              setCurrentTask(task);
              setShowEditTaskPopUp(true);
            }}
            handleDeleteTask={handleDeleteTask}
            handleCheckboxChange={handleCheckboxChange}
            handleDateChange={handleDateChange}
          />
        </div>
      </div>

      {showEditTaskPopUp && (
        <EditTaskPopUp
          task={currentTask}
          onSave={(task) => {
            handleAddTask(task);
            setShowEditTaskPopUp(false);
          }}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAdd={handleAddTask}
          onClose={() => setShowAddTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAdd={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
        />
      )}
    </div>
  );
}

export default Dashboard;
