import React from 'react';
import { useNavigate } from 'react-router-dom';
import { SignOutIcon, TextLogoIcon } from '../icons';

function Navbar() {
  const navigate = useNavigate();

  const handleSignOut = async () => {
    try {
      // Beispiel für die Abmelde-Logik
      const response = await fetch('/api/logout', {
        method: 'POST',
        credentials: 'include', // Sende Cookies zusammen mit der Anfrage
      });

      if (response.ok) {
        // Entferne Tokens oder andere Authentifizierungsdaten aus dem lokalen Speicher
        localStorage.removeItem('authToken');
        sessionStorage.removeItem('authToken');

        // Nach der Abmeldung den Benutzer zur Landing Page weiterleiten
        navigate('/');
      } else {
        throw new Error('Abmeldung fehlgeschlagen');
      }
    } catch (error) {
      console.error('Fehler bei der Abmeldung:', error);
      // Optional: Fehlerbehandlung anzeigen oder den Benutzer benachrichtigen
    }
  };

  const handleLogin = () => {
    // Entferne Tokens oder andere Authentifizierungsdaten aus dem lokalen Speicher
    localStorage.removeItem('authToken');
    sessionStorage.removeItem('authToken');

    // Navigiere zur Login-Seite
    navigate('/login');
  };

  return (
    <div className="w-full bg-white p-4 border rounded-large">
      <div className="flex justify-between items-center max-w-5xl mx-auto">
        <TextLogoIcon className="h-8" alt="Tick2gether Logo" />
        <div className="flex items-center">
          <button
            type="button"
            onClick={handleLogin}
            className="relative rounded-full bg-white text-gray-400 hover:text-gray-500 focus:outline-none"
            style={{
              width: '30px',
              height: '30px',
              flexShrink: 0,
              borderRadius: '10px',
              background: 'var(--Shade, rgba(0, 0, 0, 0.05))',
              marginRight: '10px' // Abstand zwischen den Buttons
            }}
          >
            <span className="sr-only">Login</span>
            <SignOutIcon className="h-5 w-5" aria-hidden="true" />
          </button>
          <button
            type="button"
            onClick={handleSignOut}
            className="relative rounded-full bg-white text-gray-400 hover:text-gray-500 focus:outline-none"
            style={{
              width: '30px',
              height: '30px',
              flexShrink: 0,
              borderRadius: '10px',
              background: 'var(--Shade, rgba(0, 0, 0, 0.05))'
            }}
          >
            <span className="sr-only">Sign out</span>
            <SignOutIcon className="h-5 w-5" aria-hidden="true" />
          </button>
        </div>
      </div>
    </div>
  );
}

export default Navbar;
