import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import { PlannedIcon } from '../icons';
import { fetchTasks, addTask, updateTask, deleteTask, fetchUserId, fetchEmail, addCategory, deleteCategory } from '../api';
import { connectWebSocket, disconnectWebSocket, sendWebSocketMessage } from '../websocket';

const initialCategories = [
  { id: 1, name: 'All Tasks', icon: PlannedIcon },
  { id: 2, name: 'Today', icon: PlannedIcon },
  { id: 3, name: 'Planned', icon: PlannedIcon },
  { id: 4, name: 'Any Time', icon: PlannedIcon },
  { id: 5, name: 'Work', icon: PlannedIcon },
  { id: 6, name: 'Personal', icon: PlannedIcon },
  { id: 7, name: 'Logbook', icon: PlannedIcon },
];

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState(initialCategories);
  const [selectedCategory, setSelectedCategory] = useState(initialCategories[0]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();
  const webSocketRef = useRef(null);

  useEffect(() => {
    const fetchUserAndTasks = async () => {
      try {
        const email = await fetchEmail();
        const userId = await fetchUserId(email);
        if (userId) {
          console.log('Fetched userId:', userId); // Log the userId
          setUserId(userId);
          await fetchTasksData(userId);
        } else {
          navigate('/login');
        }
      } catch (error) {
        navigate('/login');
      }
    };

    fetchUserAndTasks();
  }, [navigate]);

  useEffect(() => {
    if (userId) {
      initializeWebSocket(userId);
    }
  }, [userId]);

  useEffect(() => {
    return () => {
      disconnectWebSocket();
    };
  }, []);

  const initializeWebSocket = (userId) => {
    if (!webSocketRef.current) {
      console.log('Connecting WebSocket...');
      connectWebSocket(onTaskMessageReceived, onCategoryMessageReceived);
      webSocketRef.current = true;
    }
  };

  const fetchTasksData = async (userId) => {
    try {
      console.log('Fetching tasks');
      const tasksData = await fetchTasks(userId);
      setTasks(tasksData);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleAddNewTask = async (newTask) => {
    try {
      const addedTask = await addTask(newTask, userId);
      setShowAddTaskPopUp(false);
      sendWebSocketMessage('/topic/tasks', { type: 'TASK_ADD', task: addedTask });
      console.log('Task added successfully');

      setTasks((prevTasks) => {
        const taskExists = prevTasks.some(task => task.taskId === addedTask.taskId);
        if (!taskExists) {
          return [...prevTasks, addedTask];
        }
        return prevTasks;
      });
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      const taskToUpdate = {
        ...updatedTask,
        userId: userId,
        taskId: currentTask.taskId,
      };
      const updated = await updateTask(taskToUpdate, userId);
      setShowEditTaskPopUp(false);
      sendWebSocketMessage('/topic/tasks', { type: 'TASK_UPDATE', task: updated });
      console.log('Task updated successfully');

      setTasks((prevTasks) =>
        prevTasks.map((task) => (task.taskId === updated.taskId ? updated : task))
      );
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      sendWebSocketMessage('/topic/tasks', { type: 'TASK_DELETE', taskId: id });
      console.log('Task deleted successfully');
      setTasks((prevTasks) => prevTasks.filter((task) => task.taskId !== id));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };
  const handleCheckboxChange = async (id) => {
    console.log('Checkbox changed for ID:', id);
    const updatedTodos = todos.map(todo => {
      if (todo.taskId === id) {
        const updatedTodo = { ...todo, completed: !todo.completed };
        if (updatedTodo.completed) {
          updatedTodo.categoryId = 7; // Kategorie-ID auf 7 setzen, wenn die Aufgabe abgeschlossen ist
        }
        return updatedTodo;
      }
      return todo;
    });

    setTasks(updatedTodos);

    const updatedTask = updatedTodos.find(todo => todo.taskId === id);
    try {
      // Senden der WebSocket-Nachricht
      sendWebSocketMessage('/app/updateTask', { type: 'TASK_UPDATE', task: updatedTask });
      console.log('WebSocket update task successful');
    } catch (error) {
      console.error('Error updating task via WebSocket:', error);
    }
  };
  const handleAddCategory = async (newCategory) => {
    try {
      console.log('Adding new category:', newCategory);
      const data = await addCategory(newCategory);
      console.log('Category added successfully:', data);
      const newCategoryObject = {
        id: data.categoryId,
        name: data.name,
        icon: PlannedIcon,
        userId: data.userId,
      };
      setCategories((prevCategories) => [...prevCategories, newCategoryObject]);
      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_ADD', category: newCategoryObject });
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  const handleDeleteCategory = async (categoryId) => {
    try {
      console.log(`Deleting category with id: ${categoryId}`);
      await deleteCategory(categoryId);
      const updatedCategories = categories.filter((category) => category.id !== categoryId);
      setCategories(updatedCategories);
      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_DELETE', categoryId });
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode((prevMode) => !prevMode);
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const onTaskMessageReceived = (message) => {
    console.log('WebSocket task message received:', message);

    const { type, task, taskId } = message;
    console.log('Received message:', message);

    if (type === 'TASK_ADD' || type === 'TASK_UPDATE') {
      if (!task || !task.userId) {
        console.warn('Invalid task received:', task);
        return;
      }

      console.log('Received task userId:', task.userId);
      console.log('Current state userId:', userId);

      if (task.userId !== userId) {
        console.warn('Task userId does not match:', task.userId, userId);
        return;
      }

      setTasks((prevTasks) => {
        const taskExists = prevTasks.some(t => t.taskId === task.taskId);

        if (type === 'TASK_ADD') {
          if (!taskExists) {
            return [...prevTasks, task];
          }
        } else if (type === 'TASK_UPDATE') {
          return prevTasks.map((t) => (t.taskId === task.taskId ? task : t));
        }

        return prevTasks;
      });
    } else if (type === 'TASK_DELETE') {
      if (!taskId) {
        console.warn('Invalid taskId received for delete:', taskId);
        return;
      }

      setTasks((prevTasks) => prevTasks.filter((t) => t.taskId !== taskId));
    } else {
      console.error('Unknown message type:', type);
    }
  };

  window.addEventListener('error', (event) => {
    event.preventDefault();
    console.warn('Error caught:', event.error);
  });

  window.addEventListener('unhandledrejection', (event) => {
    event.preventDefault();
    console.warn('Unhandled promise rejection:', event.reason);
  });

  const onCategoryMessageReceived = (message) => {
    console.log('WebSocket category message received:', message);

    if (message && typeof message === 'object') {
      const { type, category, categoryId } = message;
      if (type === 'CATEGORY_ADD') {
        setCategories((prevCategories) => [...prevCategories, category]);
      } else if (type === 'CATEGORY_UPDATE') {
        setCategories((prevCategories) =>
          prevCategories.map((c) => (c.id === category.id ? category : c))
        );
      } else if (type === 'CATEGORY_DELETE') {
        setCategories((prevCategories) => prevCategories.filter((c) => c.id !== categoryId));
      }
    } else {
      console.error('Invalid category message received:', message);
    }
  };

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories onSelectCategory={setSelectedCategory} userId={userId} categories={categories} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />}
              <span className="font-bold text-grayCustom-dark">{selectedCategory.name}</span>
            </div>
            <ToDoList
              todos={tasks}
              setTasks={setTasks}
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask}
              selectedCategory={selectedCategory}
              handleDeleteTask={handleDeleteTask} // hinzugefügt
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              selectedCategoryId={selectedCategory.id}
              toggleAddCategoryModal={() => setShowAddCategoryPopUp(true)}
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          onClose={() => setShowAddTaskPopUp(false)}
          currentCategoryId={selectedCategory.id}
        />
      )}
      {showEditTaskPopUp && currentTask && (
        <EditTaskPopUp
          task={currentTask}
          onSaveTask={handleSaveTask}
          onClose={() => setShowEditTaskPopUp(false)}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}

export default Dashboard;
