import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import AddCategoryPopUp from '../components/AddCategoryPopUp';
import {
  AllTasksIcon,
  AnyTimeIcon,
  PersonalIcon,
  PlannedIcon,
  SparkleIcon,
  SparkleLightIcon,
  TodayIcon,
  WorkIcon,
  BookIcon
} from '../icons';
import { fetchTasks, addTask, updateTask, deleteTask, fetchUserId, fetchEmail, addCategory, fetchCategories } from '../api';
import { connectWebSocket, disconnectWebSocket } from '../websocket';

const iconMap = {
  'All Tasks': AllTasksIcon,
  'Today': TodayIcon,
  'Planned': PlannedIcon,
  'Any Time': AnyTimeIcon,
  'Work': WorkIcon,
  'Personal': PersonalIcon,
  'Logbook': BookIcon,
};

const customCategoryIcon = SparkleIcon;
const customCategoryLightIcon = SparkleLightIcon;

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();
  const webSocketRef = useRef(null);

  const initializeWebSocket = (userId) => {
    if (webSocketRef.current) {
      console.log('WebSocket already connected');
      return;
    }
    connectWebSocket(onTaskMessageReceived, onCategoryMessageReceived, userId);
    webSocketRef.current = true; // Markiere, dass WebSocket verbunden ist
  };

  useEffect(() => {
    if (userId) {
      initializeWebSocket(userId);
    }

    return () => {
      disconnectWebSocket();
      webSocketRef.current = null;
    };
  }, [userId]);

  useEffect(() => {
    const fetchUserAndTasksAndCategories = async () => {
      try {
        const email = await fetchEmail();
        const fetchedUserId = await fetchUserId(email);
        if (fetchedUserId) {
          setUserId(fetchedUserId);

          const tasks = await fetchTasks();
          setTasks(tasks);

          const fetchedCategories = await fetchCategories(fetchedUserId);
          const mappedCategories = fetchedCategories.map(category => ({
            ...category,
            id: category.categoryId,
            icon: iconMap[category.name] || customCategoryIcon,
            isSelected: false,
            fullWidth: false,
            showDelete: false,
          }));
          setCategories(mappedCategories);
          if (mappedCategories.length > 0) {
            setSelectedCategory(mappedCategories[0]);
          }
        } else {
          navigate('/login');
        }
      } catch (error) {
        console.error('Error fetching user, tasks, or categories:', error);
        navigate('/login');
      }
    };

    fetchUserAndTasksAndCategories();
  }, [navigate]);

  const handleAddNewTask = async (newTask) => {
    try {
      const formattedTask = { ...newTask, categoryId: Number(newTask.categoryId) };
      const addedTask = await addTask(formattedTask, userId);
      setShowAddTaskPopUp(false);

      // Kein WebSocket-Nachricht senden, da nur Updates empfangen werden
      setTasks(prevTasks => [...prevTasks, addedTask]);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      const taskToUpdate = { ...updatedTask, userId: userId, taskId: currentTask.taskId };
      const updated = await updateTask(taskToUpdate, userId);
      setShowEditTaskPopUp(false);

      setTasks(prevTasks =>
        prevTasks.map(task => (task.taskId === updated.taskId ? updated : task))
      );
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId, userId);
      setTasks(prevTasks => prevTasks.filter(task => task.taskId !== taskId));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  const onTaskMessageReceived = (message) => {
    console.log('Task update received:', message);
    // Hier kannst du Logik hinzuf端gen, um empfangene Aufgaben-Updates zu verarbeiten
    setTasks(prevTasks => [...prevTasks, message]);
  };

  const onCategoryMessageReceived = (message) => {
    console.log('Category update received:', message);
    // Hier kannst du Logik hinzuf端gen, um empfangene Kategorisierungs-Updates zu verarbeiten
    setCategories(prevCategories => [...prevCategories, message]);
  };

  return (
    <div>
      <Navbar />
      <Categories categories={categories} />
      <ToDoList tasks={tasks} onEdit={setCurrentTask} onDelete={handleDeleteTask} />
      <ToDoActions
        onAddTask={() => setShowAddTaskPopUp(true)}
        onAddCategory={() => setShowAddCategoryPopUp(true)}
        isDeleteMode={isDeleteMode}
        setIsDeleteMode={setIsDeleteMode}
      />
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onClose={() => setShowAddTaskPopUp(false)}
          onAddTask={handleAddNewTask}
        />
      )}
      {showEditTaskPopUp && (
        <EditTaskPopUp
          task={currentTask}
          onClose={() => setShowEditTaskPopUp(false)}
          onSave={handleSaveTask}
        />
      )}
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onClose={() => setShowAddCategoryPopUp(false)}
          onAddCategory={addCategory}
        />
      )}
    </div>
  );
}

export default Dashboard;
