export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  categories.forEach(category => {
    switch (category.categoryId) {
      case 1:
        // Alle Aufgaben
        categorizedTasks[category.categoryId] = tasks.filter(task => !task.completed);
        break;
      case 2:
        // Heute
        const today = new Date().toISOString().split('T')[0];
        categorizedTasks[category.categoryId] = tasks.filter(task => task.dueDate === today && !task.completed);
        break;
      case 3:
        // Geplant
        categorizedTasks[category.categoryId] = tasks.filter(task => task.planDate && !task.completed);
        break;
      case 4:
        // Jederzeit
        categorizedTasks[category.categoryId] = tasks.filter(task => !task.dueDate && !task.completed);
        break;
      case 5:
        // Arbeit
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === 5 && !task.completed);
        break;
      case 6:
        // Persönlich
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === 6 && !task.completed);
        break;
      case 7:
        // Logbuch
        categorizedTasks[category.categoryId] = tasks.filter(task => task.completed);
        break;
      default:
        // Benutzerdefinierte Kategorien
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId && !task.completed);
        break;
    }
  });

  return categorizedTasks;
};
