import { useState, useEffect, useCallback } from 'react';
import { fetchCategories, addCategory } from '../api';
import { sendWebSocketMessage } from '../websocket';
import { iconMap } from '../icons'; // Importiere iconMap

export function useCategories(userId) {
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);

  const initializeCategories = useCallback(async () => {
    try {
      const fetchedCategories = await fetchCategories(userId);
      const mappedCategories = fetchedCategories.map(category => ({
        ...category,
        id: category.categoryId,
        icon: iconMap[category.name] || iconMap['Custom Category'], // Fallback auf CustomCategoryIcon
      }));
      setCategories(mappedCategories);
      if (mappedCategories.length > 0) {
        setSelectedCategory(mappedCategories[0]);
      }
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  }, [userId]);

  useEffect(() => {
    if (userId) {
      initializeCategories();
    }
  }, [initializeCategories, userId]);

  const handleAddCategory = async (newCategory) => {
    try {
      const categoryExists = categories.some(cat => cat.name === newCategory.name);
      if (categoryExists) {
        console.warn('Category already exists:', newCategory);
        return;
      }
      const data = await addCategory(newCategory);
      const newCategoryObject = {
        id: data.categoryId,
        name: data.name,
        icon: iconMap['Custom Category'], // Verwende CustomCategoryIcon
      };
      sendWebSocketMessage(`/app/updateCategory/${newCategoryObject.id}`, { type: 'CATEGORY_UPDATE', category: newCategoryObject });
      setCategories(prevCategories => [...prevCategories, newCategoryObject]);
    } catch (error) {
      console.error('Error adding category:', error);
    }
  };

  return {
    categories,
    selectedCategory,
    setSelectedCategory,
    handleAddCategory,
  };
}
