import React, { useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
import { connectWebSocket, disconnectWebSocket } from '../websocket';

const ShareHandler = () => {
  const { token } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const checkAuthAndFetchDetails = async () => {
      const jwtToken = Cookies.get('jwtToken');
      const isLoggedIn = !!jwtToken;

      if (!isLoggedIn) {
        navigate(`/login?returnUrl=/share/${token}`);
      } else {
        try {
          const response = await axios.get(`http://localhost:8080/api/v1/token/share/${token}`, {
            headers: {
              Authorization: `Bearer ${jwtToken}`,
            },
          });
          console.log('Share details:', response.data); // Optional: Entfernen oder umwandeln in ein informatives UI-Element
          const { userId, sharedTaskId, sharedCategoryId } = response.data;

          // WebSocket-Verbindung herstellen
          connectWebSocket(userId, onTaskMessageReceived, onCategoryMessageReceived);

          // Weiterleitung zum Dashboard nach erfolgreicher Verbindung
          navigate('/dashboard');
        } catch (error) {
          if (error.response && error.response.status === 401) {
            navigate(`/login?returnUrl=/share/${token}`);
          } else {
            console.error('Error fetching share details:', error);
            // Optional: Zeigen Sie eine Benachrichtigung für den Benutzer an
          }
        }
      }
    };

    checkAuthAndFetchDetails();

    return () => {
      // WebSocket-Verbindung trennen
      disconnectWebSocket();
    };
  }, [token, navigate]);

  return (
    <div>
      <h1>Processing Share Link...</h1>
    </div>
  );
};

const onTaskMessageReceived = (message) => {
  console.log('Task message received:', message); // Debugging-Log hinzufügen
  const { type, task, taskId } = message;

  if (type === 'TASK_UPDATE' && task) {
    console.log('Processing TASK_UPDATE:', task); // Debugging-Log hinzufügen
    setTasks(prevTasks => {
      const taskExists = prevTasks.some(t => t.taskId === task.taskId);
      console.log(`Task ${task.taskId} exists:`, taskExists);
      if (taskExists) {
        return prevTasks.map(t => (t.taskId === task.taskId ? task : t));
      } else {
        return [...prevTasks, task];
      }
    });
  } else if (type === 'TASK_DELETE' && taskId) {
    console.log('Processing TASK_DELETE for taskId:', taskId); // Debugging-Log hinzufügen
    setTasks(prevTasks => prevTasks.filter(t => t.taskId !== taskId));
  } else {
    console.log('Unknown task message type or missing task data:', type);
  }
};

const onCategoryMessageReceived = (message) => {
  console.log('Category message received:', message); // Debugging-Log hinzufügen
  const { type, category, categoryId } = message;

  if (type === 'CATEGORY_UPDATE' && category) {
    console.log('Processing CATEGORY_UPDATE:', category); // Debugging-Log hinzufügen
    setCategories(prevCategories =>
      prevCategories.map(c => (c.id === category.categoryId ? { ...category, id: category.categoryId, icon: iconMap[category.name] || customCategoryIcon } : c))
    );
  } else if (type === 'CATEGORY_DELETE' && categoryId) {
    console.log('Processing CATEGORY_DELETE for categoryId:', categoryId); // Debugging-Log hinzufügen
    setCategories(prevCategories => prevCategories.filter(c => c.id !== categoryId));
  } else {
    console.log('Unknown category message type or missing category data:', type);
  }
};

export default ShareHandler;
