export const categorizeTasks = (tasks, categories) => {
  const categorizedTasks = {};

  categories.forEach(category => {
    switch (category.categoryId) {
      case 1:
        // All Tasks
        categorizedTasks[category.categoryId] = tasks;
        break;
      case 2:
        // Today
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId);
        break;
      case 3:
        // Planned
        categorizedTasks[category.categoryId] = tasks.filter(task => task.planDate);
        break;
      case 4:
        // Any Time
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId);
        break;
      case 5:
        // Work
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId);
        break;
      case 6:
        // Personal
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId);
        break;
      case 7:
        // Logbook
        categorizedTasks[category.categoryId] = tasks.filter(task => task.completed);
        break;
      default:
        // Benutzerdefinierte Kategorien
        categorizedTasks[category.categoryId] = tasks.filter(task => task.categoryId === category.categoryId);
        break;
    }
  });

  return categorizedTasks;
};
