import React from 'react';
import ToDo from './ToDo';
import { startOfToday, isSameDay, isAfter, isBefore } from 'date-fns';

export default function ToDoList({ todos = [], setTodos, isDeleteMode, initialUsers, onEditTodo, onDateChange, selectedCategory }) {
  const handleCheckboxChange = (id) => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        if (selectedCategory.name === 'Today') {
          return { ...todo, completed: !todo.completed, originalCategory: 'Today', category: !todo.completed ? 'Logbook' : 'Today' };
        } else if (selectedCategory.name === 'Logbook') {
          return { ...todo, completed: !todo.completed, category: !todo.completed ? todo.originalCategory || 'Today' : 'Logbook' };
        } else {
          return { ...todo, completed: !todo.completed, originalCategory: todo.category, category: todo.completed ? todo.category : 'Logbook' };
        }
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const handleDelete = (id) => {
    const updatedTodos = todos.filter(todo => todo.id !== id);
    setTodos(updatedTodos);
  };

  const handleTodoClick = (todo, e) => {
    if (e.target.tagName === 'LABEL') {
      onEditTodo(todo);
    } else {
      setTodos(todos.map(t => 
        t.id === todo.id ? { ...t, selected: !t.selected } : { ...t, selected: false }
      ));
    }
  };

  const handleEditClick = (todo) => {
    onEditTodo(todo);
  };

  const handleDateChange = (id, date, purpose) => {
    const updatedTodos = todos.map(todo => {
      if (todo.id === id) {
        if (purpose === 'plan') {
          return { ...todo, planDate: date };
        } else if (purpose === 'deadline') {
          return { ...todo, hasCalendar: !!date, deadlineDate: date };
        }
      }
      return todo;
    });
    setTodos(updatedTodos);
  };

  const today = startOfToday();

  const filteredTodos = todos.filter(todo => {
    if (selectedCategory.name === 'Planned') {
      return todo.planDate && !todo.completed && isAfter(new Date(todo.planDate), today);
    }
    if (selectedCategory.name === 'Today' || selectedCategory.name === 'Personal' || selectedCategory.name === 'Work' || selectedCategory.name === 'Any Time') {
      return (todo.category === selectedCategory.name && (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)));
    }
    if (selectedCategory.name === 'All Tasks') {
      return (!todo.planDate || isSameDay(new Date(todo.planDate), today) || isBefore(new Date(todo.planDate), today)) && todo.category !== 'Planned';
    }
    if (selectedCategory.name === 'Logbook') {
      return todo.category === 'Logbook';
    }
    return todo.category === selectedCategory.name;
  });

  return (
    <fieldset>
      <legend className="sr-only">Tasks</legend>
      <div className="space-y-1">
        {filteredTodos.map(todo => (
          <ToDo
            key={todo.id}
            todo={todo}
            handleCheckboxChange={handleCheckboxChange}
            handleTodoClick={handleTodoClick}
            handleDelete={handleDelete}
            handleEditClick={handleEditClick}
            isDeleteMode={isDeleteMode}
            initialUsers={initialUsers}
            onDateChange={handleDateChange}
            isTodayCategory={selectedCategory.name === 'Today'}
          />
        ))}
      </div>
    </fieldset>
  );
}
