import React, { useState, useEffect } from 'react';
import {
  AllTasksIcon,
  AnyTimeIcon,
  PersonalIcon,
  TodayIcon,
  WorkIcon,
  SparkleIcon,
  SparkleLightIcon,
  XIcon,
  DeleteIcon,
  AllTasksLightIcon,
  AnyTimeLightIcon,
  PersonalLightIcon,
  PlannedIcon,
  PlannedLightIcon,
  TodayLightIcon,
  WorkLightIcon,
  BookIcon,
  BookLightIcon,
  AddIcon
} from '../icons';
import AddCategoryPopUp from './AddCategoryPopUp';
import { addCategory, deleteCategory as apiDeleteCategory } from '../api';
import { sendWebSocketMessage } from '../websocket';

const initialCategories = [];

const iconMap = {
  'All Tasks': AllTasksIcon,
  'Today': TodayIcon,
  'Planned': PlannedIcon,
  'Any Time': AnyTimeIcon,
  'Work': WorkIcon,
  'Personal': PersonalIcon,
  'Logbook': BookIcon,
};

const lightIconMap = {
  'All Tasks': AllTasksLightIcon,
  'Today': TodayLightIcon,
  'Planned': PlannedLightIcon,
  'Any Time': AnyTimeLightIcon,
  'Work': WorkLightIcon,
  'Personal': PersonalLightIcon,
  'Logbook': BookLightIcon,
};

const customCategoryIcon = SparkleIcon;
const customCategoryLightIcon = SparkleLightIcon;

function Categories({ onSelectCategory, userId, categoriesFromBackend }) {
  const [categories, setCategories] = useState(initialCategories);
  const [additionalCategories, setAdditionalCategories] = useState(Array.isArray(categoriesFromBackend) ? categoriesFromBackend : []);
  const [showDeleteIcons, setShowDeleteIcons] = useState(false);
  const [showAddCategoryPopUp, setShowAddCategoryPopUp] = useState(false);

  useEffect(() => {
    console.log('Initial categories:', categories);
    console.log('Additional categories:', additionalCategories);
  }, [categories, additionalCategories]);

  useEffect(() => {
    setAdditionalCategories(Array.isArray(categoriesFromBackend) ? categoriesFromBackend : []);
  }, [categoriesFromBackend]);

  const selectCategory = (categoryId) => {
    const updateSelection = (categoryList) => categoryList.map(category => ({
      ...category,
      isSelected: category.id === categoryId,
    }));

    setCategories(updateSelection(categories));
    setAdditionalCategories(updateSelection(additionalCategories));

    const selectedCategory = [...categories, ...additionalCategories].find(category => category.id === categoryId);
    console.log('Selected category:', selectedCategory);
    onSelectCategory(selectedCategory);
  };

  const handleAddCategory = async (categoryData) => {
    try {
      const data = await addCategory({ ...categoryData, userId }); // Ensure userId is included
      console.log('Category added:', data);
      const newCategory = {
        id: data.categoryId, 
        name: data.name,
        icon: customCategoryIcon,
        lightIcon: customCategoryLightIcon,
        color: 'text-gray-400',
        selectedColor: 'text-white',
        isSelected: false,
        fullWidth: false,
        showDelete: false,
        userId: data.userId,
      };

      // Check if the category already exists
      setAdditionalCategories(prevCategories => {
        const categoryExists = prevCategories.some(cat => cat.id === newCategory.id);
        if (!categoryExists) {
          return [...prevCategories, newCategory];
        }
        return prevCategories;
      });

      // Send WebSocket message to update the added category
      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_ADD', category: newCategory });
    } catch (error) {
      console.error('Error adding category:', error);
    } finally {
      setShowAddCategoryPopUp(false);
    }
  };

  const toggleDeleteIcons = () => {
    const toggleShowDelete = (categoryList) => categoryList.map(category => ({
      ...category,
      showDelete: !showDeleteIcons,
    }));

    setCategories(toggleShowDelete(categories));
    setAdditionalCategories(toggleShowDelete(additionalCategories));
    setShowDeleteIcons(!showDeleteIcons);
  };

  const handleDeleteCategory = async (categoryId) => {
    try {
      await apiDeleteCategory(categoryId);
      setAdditionalCategories(additionalCategories.filter(cat => cat.id !== categoryId));

      // Send WebSocket message to update the deleted category
      sendWebSocketMessage('/topic/categories', { type: 'CATEGORY_DELETE', categoryId });
    } catch (error) {
      console.error('Error deleting category:', error);
    }
  };

  // Combine categories and additionalCategories, ensuring unique ids
  const allCategories = [...categories, ...additionalCategories].reduce((unique, category) => {
    if (!unique.some(cat => cat.id === category.id)) {
      unique.push({
        id: category.id,
        name: category.name,
        isSelected: category.isSelected || false,
        icon: category.icon || iconMap[category.name] || customCategoryIcon,
        lightIcon: category.lightIcon || lightIconMap[category.name] || customCategoryLightIcon,
        color: category.color || 'text-gray-400',
        selectedColor: category.selectedColor || 'text-white',
        fullWidth: category.fullWidth || false,
        showDelete: category.showDelete || false,
      });
    }
    return unique;
  }, []);

  const isMaxCategories = allCategories.length >= 12;

  return (
    <div>
      <div className="grid grid-cols-2 gap-4">
        {allCategories.map((category) => {
          const bgColor = category.isSelected ? '#0374FA' : '#0000000D';
          const ringColor = category.isSelected ? '#0374FA26' : '';
          const textColor = category.isSelected ? 'text-white' : 'text-gray-900';
          const IconComponent = category.isSelected ? category.lightIcon : category.icon;

          return (
            <div
              key={category.id}
              className={`relative flex ${category.fullWidth ? 'flex-row' : 'flex-col'} justify-center items-center rounded-lg ${category.isSelected ? 'ring-2 ring-offset-2' : ''} ${category.fullWidth ? 'col-span-2' : ''}`}
              style={{
                minWidth: category.fullWidth ? '100%' : '140px',
                maxWidth: '100%',
                height: category.fullWidth ? '60px' : '100px',
                borderRadius: '20px',
                backgroundColor: bgColor,
                ringColor: ringColor,
                padding: category.fullWidth ? '0 15px' : '15px',
              }}
              onClick={() => selectCategory(category.id)}
            >
              {category.fullWidth ? (
                <>
                  <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  <span className={`ml-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              ) : (
                <>
                  <div className="absolute top-2 left-2 p-2">
                    <IconComponent className={`h-6 w-6 ${category.isSelected ? category.selectedColor : category.color}`} />
                  </div>
                  <span className={`absolute bottom-2 left-2 p-2 text-sm font-medium ${textColor}`}>{category.name}</span>
                </>
              )}
              {category.showDelete && (
                <button className="absolute top-2 right-2 p-2" onClick={(e) => { e.stopPropagation(); handleDeleteCategory(category.id); }}>
                  <XIcon className="h-5 w-5" />
                </button>
              )}
            </div>
          );
        })}
        <div className="col-span-2 flex items-center justify-center mt-3 gap-3">
          <div className={`flex-grow flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer h-[50px] ${isMaxCategories ? 'opacity-50' : ''}`}
            style={{
              borderRadius: '20px',
              backgroundColor: '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={() => setShowAddCategoryPopUp(true)}
          >
            <div className="flex items-center space-x-2 w-full justify-center">
              <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
                <AddIcon className="h-6 w-6 text-gray-400" />
              </div>
              <span className="text-sm font-medium text-gray-900">Add Category</span>
            </div>
          </div>
          <div className="flex-shrink-0 flex items-center justify-center rounded-lg bg-grayCustom.dark hover:bg-grayCustom.light focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blueCustom.DEFAULT cursor-pointer w-[50px] h-[50px]"
            style={{
              borderRadius: '20px',
              backgroundColor: showDeleteIcons ? '#FC5E5E26' : '#0000000D',
              padding: '0 15px',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
            }}
            onClick={toggleDeleteIcons}
          >
            <div className="flex items-center justify-center" style={{ width: '20px', height: '20px' }}>
              <DeleteIcon className="h-6 w-6" />
            </div>
          </div>
        </div>
      </div>
      {showAddCategoryPopUp && (
        <AddCategoryPopUp
          onAddCategory={handleAddCategory}
          onClose={() => setShowAddCategoryPopUp(false)}
          userId={userId}
        />
      )}
    </div>
  );
}

export default Categories;
