import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Categories from '../components/Categories';
import ToDoList from '../components/ToDoList';
import ToDoActions from '../components/ToDoActions';
import Navbar from '../components/Navbar';
import EditTaskPopUp from '../components/EditTaskPopUp';
import AddTaskPopUp from '../components/AddTaskPopUp';
import { PlannedIcon } from '../icons';
import { fetchTasks, addTask, updateTask, deleteTask } from '../api';

const initialCategories = [
  { id: 1, name: 'All Tasks', icon: PlannedIcon },
  { id: 2, name: 'Today', icon: PlannedIcon },
  { id: 3, name: 'Planned', icon: PlannedIcon },
  { id: 4, name: 'Any Time', icon: PlannedIcon },
  { id: 5, name: 'Work', icon: PlannedIcon },
  { id: 6, name: 'Personal', icon: PlannedIcon },
  { id: 7, name: 'Logbook', icon: PlannedIcon },
];

function Dashboard() {
  const [showEditTaskPopUp, setShowEditTaskPopUp] = useState(false);
  const [isDeleteMode, setIsDeleteMode] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(initialCategories[0]);
  const [currentTask, setCurrentTask] = useState(null);
  const [showAddTaskPopUp, setShowAddTaskPopUp] = useState(false);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    fetchUserId();
  }, []);

  const fetchUserId = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/v1/user/me', { withCredentials: true });
      const user = response.data;
      setUserId(user.userId);
      fetchTasksData(user.userId);
    } catch (error) {
      console.error('============================ Error fetching user ID:', error);
      // Handle error appropriately, maybe redirect to login or show an error message
    }
  };

  const fetchTasksData = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:8080/api/v1/task/getTasks?userId=${userId}`);
      const tasksData = response.data;
      console.log('Received tasks:', tasksData);
      if (Array.isArray(tasksData)) {
        setTasks(tasksData);
      } else {
        console.error('Expected an array but received:', tasksData);
      }
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  const handleAddNewTask = async (newTask) => {
    try {
      newTask.userId = userId; // Ensure userId is assigned correctly
      const data = await addTask(newTask);
      setTasks(prevTasks => [...prevTasks, data]); // Functional update to ensure correct state update
      await fetchTasksData(userId); // Fetch tasks after adding new task
      setShowAddTaskPopUp(false);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  const handleSaveTask = async (updatedTask) => {
    try {
      await updateTask(updatedTask);
      await fetchTasksData(userId); // Fetch tasks after update
      setShowEditTaskPopUp(false);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const toggleDeleteMode = () => {
    setIsDeleteMode(prevMode => !prevMode); // Toggle delete mode
  };

  const handleEditTask = (task) => {
    setCurrentTask(task);
    setShowEditTaskPopUp(true);
  };

  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id);
      await fetchTasksData(userId); // Fetch tasks after delete
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  if (userId === null) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto px-4 sm:px-10 lg:px-8 mt-8">
      <div className="w-full">
        <div className="flex justify-between items-center max-w-5xl mx-auto rounded-2xl">
          <Navbar />
        </div>
      </div>
      <div className="h-16" />
      <div className="flex flex-col lg:flex-row items-center lg:items-start justify-center w-full max-w-5xl mx-auto">
        <div className="w-full lg:w-1/3 lg:mr-8 mb-8 lg:mb-0">
          <Categories categories={initialCategories} onSelectCategory={setSelectedCategory} />
        </div>
        <div className="flex-1 w-full">
          <div className="bg-white border p-8 rounded-large flex flex-col justify-between h-full">
            <div className="flex items-center gap-1.5 mb-4">
              {selectedCategory.icon && <selectedCategory.icon className="h-6 w-6" />} {/* Dynamically render icon */}
              <span className="font-bold text-grayCustom-dark">{selectedCategory.name}</span>
            </div>
            <ToDoList
              todos={tasks} // Correct prop name
              setTodos={setTasks} // Correct prop name
              isDeleteMode={isDeleteMode}
              onEditTodo={handleEditTask} // Correct callback name
              selectedCategory={selectedCategory} // Pass the entire selectedCategory object
              fetchTasksData={fetchTasksData} // Pass the fetchTasksData function
            />
            <ToDoActions
              toggleAddTaskModal={() => setShowAddTaskPopUp(true)}
              toggleDeleteMode={toggleDeleteMode}
              selectedCategoryId={selectedCategory.id} // Pass the selectedCategoryId to ToDoActions
            />
          </div>
        </div>
      </div>
      {showAddTaskPopUp && (
        <AddTaskPopUp
          onAddTask={handleAddNewTask}
          onClose={() => setShowAddTaskPopUp(false)}
          currentCategoryId={selectedCategory.id} // Ensure currentCategoryId is passed correctly if needed
          creatorUserId={userId} // Ensure userId is passed correctly
        />
      )}
      {showEditTaskPopUp && currentTask && (
        <EditTaskPopUp task={currentTask} onSaveTask={handleSaveTask} onClose={() => setShowEditTaskPopUp(false)} />
      )}
    </div>
  );
}

export default Dashboard;
